        if ( sched_poll->nr_ports > 128 )
            return -EINVAL;

        if ( !guest_handle_okay(sched_poll->ports, sched_poll->nr_ports) )
            return -EFAULT;

        set_bit(_VPF_blocked, &v->pause_flags);
        v->poll_evtchn = -1;
        set_bit(v->vcpu_id, d->poll_mask);

        arch_vcpu_block(v);

