		break;

	case PACKET_HANDSHAKE:
		fastd_handshake_handle(sock, local_addr, remote_addr, peer, buffer);
		break;

	default:
		fastd_buffer_free(buffer);
		pr_debug("received packet with invalid type from %P[%I]", peer, remote_addr);
	}
}
